import React, { useState, FC, useRef } from 'react';

// Import React FilePond
import { FilePond, registerPlugin, setOptions } from 'react-filepond';

// Import FilePond styles
import 'filepond/dist/filepond.min.css';

// Import the Image EXIF Orientation and Image Preview plugins
// Note: These need to be installed separately
import FilePondPluginImageExifOrientation from 'filepond-plugin-image-exif-orientation';
import FilePondPluginImagePreview from 'filepond-plugin-image-preview';
import 'filepond-plugin-image-preview/dist/filepond-plugin-image-preview.css';
import { FileUploadProps } from '../types';
import { API_BASE, CONTEXT } from '../APIUrls';
import FilePondPluginFileRename from 'filepond-plugin-file-rename';

// Register the plugins
registerPlugin(FilePondPluginImageExifOrientation, FilePondPluginImagePreview,FilePondPluginFileRename);


// Our app
const ImageFile: FC<FileUploadProps> = ({onprocessfile}) => {
    //const [files, setFiles] = useState<FilePondFile[]>([]);
    const pond = useRef<any>();
    const { token } = JSON.parse(localStorage.getItem('auth')!);
    const handleInit = () => {
        // console.log('FilePond instance has initialised', pond);
    };


    FilePond.setOptions({
      fileRenameFunction: (file:any) => {
          return `my_new_name${file.extension}`;
      }
  })

    return (
        <div className="App">
            <FilePond

                ref={pond}
                //files={this.state.files}
                allowMultiple={false}
                allowReorder={true}
                accepted-file-types="image/jpeg, image/png"
                maxFiles={1}

                server={{
                    url: `${API_BASE}${CONTEXT}/upload`,
                    headers: {
                        'x-nideshop-token': token,
                    }
                }}
                name="files"
                oninit={() => handleInit()}
                fileRenameFunction={(file:string)=>{}}
                onprocessfile={onprocessfile}
            />
        </div>
    );
};

export default ImageFile;
